package com.healthmetrix.connector.commons.secrets

import com.healthmetrix.connector.commons.encodeBase64
import com.healthmetrix.connector.commons.json

class MockSecrets(private val postgresPassword: String) : SecretFetcher {
    override fun invoke(secretId: String): String? = when (secretId) {
        OUTBOX_INVITATION_TOKEN_ENCRYPTION_KEY_SECRET -> ByteArray(16).encodeBase64().string
        OUTBOX_DATABASE_ENCRYPTION_AES_KEY -> ByteArray(16).encodeBase64().string
        OUTBOX_ADD_CASE_ENCRYPTION_KEY_SECRET -> json {
            "public" to ADD_CASE_ENCRYPTION_KEY_B64
            "private" to ADD_CASE_DECRYPTION_KEY_B64
        }.toString()
        OUTBOX_POSTGRES_PASSWORD -> postgresPassword
        OUTBOX_MAILJET_SECRET_ID, OUTBOX_MAILJET_SMS_TOKEN_SECRET_ID -> json {
            "key" to "mailjet key"
            "secret" to "mailjet secret"
            "token" to "mailjet sms token"
        }.toString()
        INBOX_POSTGRES_PASSWORD -> postgresPassword
        INBOX_DATABASE_ENCRYPTION_AES_KEY -> ByteArray(16).encodeBase64().string
        INBOX_OAUTH_CLIENT_SECRET_ID, OUTBOX_OAUTH_CLIENT_SECRET_ID -> json {
            "id" to "client id"
            "secret" to "sshhhh"
            "redirect_uri" to "https://localhost:8080/v1/api/pairing/success"
        }.toString()
        OUTBOX_GATEWAY_BASIC_AUTH -> json {
            "user" to ""
            "password" to ""
        }.toString()
        else -> MOCK_SECRET_VALUE // or throw??
    }
}

private const val MOCK_SECRET_VALUE = "mock_secret"
private const val ADD_CASE_ENCRYPTION_KEY_B64 =
    ""
private const val ADD_CASE_DECRYPTION_KEY_B64 =
    ""
